query:
mutation addPaymentBreakdownForLead($amount: Float, $cancellationAmount: Float, $leadId: Int, $paymentType: String, $remarks: String, $payeeType: String) {
  addPaymentBreakdownForLead(
    amount: $amount
    cancellationAmount: $cancellationAmount
    leadId: $leadId
    paymentType: $paymentType
    remarks: $remarks
    payeeType: $payeeType
  ) {
    data
    error
    __typename
  }
}
