query:
mutation addPaymentRequestForLead($amount: Float, $paymentBreakdownId: Int, $bankAccountNumber: String, $ifsc: String, $payment_mode: String) {
  addPaymentRequestForLead(
    amount: $amount
    paymentBreakdownId: $paymentBreakdownId
    bankAccountNumber: $bankAccountNumber
    ifsc: $ifsc
    paymentMode: $payment_mode
  ) {
    error
    data
    __typename
  }
}
