mutation addSupplyToRefurbMovement($leadId: Int!, $stockInDate: DateTime, $expectedRefurbLocationId: Int, $expectedRefurbDate: DateTime, $commentId: Int, $stockInOdometerReading: Int, $stockInOdometerPhoto: Upload, $stockInLocation: String) {
  addSupplyToRefurbMovement(
    commentId: $commentId
    leadId: $leadId
    stockInDate: $stockInDate
    expectedRefurbLocationId: $expectedRefurbLocationId
    expectedRefurbDate: $expectedRefurbDate
    stockInOdometerPhoto: $stockInOdometerPhoto
    stockInOdometerReading: $stockInOdometerReading
    stockInLocation: $stockInLocation
  ) {
    ok
    message
    __typename
  }
}
