mutation updatePaymentRequestForLead($paymentRequestId: Int, $status: String, $sellLeadId: Int, $remarks: String) {
  updatePaymentRequestForLead(
    paymentRequestId: $paymentRequestId
    status: $status
    sellLeadId: $sellLeadId
    remarks: $remarks
  ) {
    error
    data
    __typename
  }
}
