query:
mutation updateSupplyToRefurbMovement($leadId: Int!, $refurbAcceptedAt: DateTime, $refurbLocationId: Int!, $carPhoto: Upload, $deliveryOdometerPhoto: Upload, $deliveryOdometerReading: Int!) {
  updateSupplyToRefurbMovement(
    leadId: $leadId
    refurbAcceptedAt: $refurbAcceptedAt
    refurbLocationId: $refurbLocationId
    deliveryOdometerReading: $deliveryOdometerReading
    deliveryOdometerPhoto: $deliveryOdometerPhoto
    carPhoto: $carPhoto
  ) {
    ok
    message
    __typename
  }
}
